import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink, useLocation } from 'react-router-dom';
import ProductList from './components/ProductList';
import OrderCreation from './components/OrderCreation';
import OrderList from './components/OrderList';
import OrderDetails from './components/OrderDetails';
import './assets/css/App.css';
import logo from './assets/images/Black-car-cleaning-sponge.jpg';
import { CSSTransition, SwitchTransition } from 'react-transition-group';

function App() {
    const location = useLocation();
    const [imageLoaded, setImageLoaded] = useState(false);

    useEffect(() => {
        const img = new Image();
        img.src = logo;
        img.onload = () => setImageLoaded(true);
    }, []);

    return (
        <div className="App">
            <div className="image-container">
                {!imageLoaded && <div className="image-placeholder">Loading...</div>}
                <img
                    src={logo}
                    alt="Logo"
                    className={`logo-image ${imageLoaded ? 'loaded' : 'loading'}`}
                    onLoad={() => setImageLoaded(true)}
                />
                <h1 className="company-title">Car Clean Co.</h1>
            </div>
            <nav>
                <ul>
                    <li>
                        <NavLink to="/" className={({ isActive }) => isActive ? "active-link" : ""}>
                            Inventory
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to="/create-order" className={({ isActive }) => isActive ? "active-link" : ""}>
                            Create Order
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to="/orders" className={({ isActive }) => isActive ? "active-link" : ""}>
                            Orders
                        </NavLink>
                    </li>
                </ul>
            </nav>
            <SwitchTransition>
                <CSSTransition key={location.pathname} classNames="page" timeout={300} >
                    <Routes location={location}>
                        <Route path="/" element={<ProductList />} />
                        <Route path="/create-order" element={<OrderCreation />} />
                        <Route path="/orders" element={<OrderList />} />
                        <Route path="/order-details/:orderId" element={<OrderDetails />} />
                    </Routes>
                </CSSTransition>
            </SwitchTransition>
        </div>
    );
}

export default function WrappedApp() {
    return (
        <Router>
            <App />
        </Router>
    );
}
