import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../assets/css/OrderDetails.css';

function OrderDetails() {
    const { orderId } = useParams();
    const [order, setOrder] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (orderId) {
            fetchOrderDetails(orderId);
        }
    }, [orderId]);

    const fetchOrderDetails = async (id) => {
        try {
            setLoading(true);
            const response = await fetch(`/api/orders/${id}`);
            if (!response.ok) {
                throw new Error('Failed to fetch order details');
            }
            const data = await response.json();
            setOrder(data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching order details:', error);
            setError('Failed to load order details. Please try again.');
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="order-details-container loading-container">
                <div className="loading-spinner"></div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="order-details-container error-container">
                <p className="error-message">{error}</p>
            </div>
        );
    }

    if (!order) {
        return (
            <div className="order-details-container error-container">
                <p className="error-message">No order found with ID <span className="highlight">{orderId}</span></p>
            </div>
        );
    }

    return (
        <div className="order-details-container">
            <h2 className="order-details-title">Order Details</h2>
            <div className="order-info">
                <p><strong>Order ID:</strong> <span className="highlight">{order.orderId}</span></p>
                <p><strong>Customer Name:</strong> {order.customerName}</p>
                <p><strong>Product Name:</strong> {order.product.productName}</p>
                <p><strong>Description:</strong> {order.product.description}</p>
                <p><strong>Quantity:</strong> {order.quantity}</p>
                <p><strong>Total Price:</strong> <span className="highlight">${(order.quantity * order.product.productPrice).toFixed(2)}</span></p>
                <p><strong>Order Date:</strong> {new Date(order.orderDate).toLocaleDateString()}</p>
            </div>
        </div>
    );
}

export default OrderDetails;
